# %% ../nbs/00_client.ipynb 13
@patch
def buy_now(self: Amazon, asin: str, shipping_address: Dict,
            user: Dict, product_url: str = None, quantity: int = 1) -> Dict:
    """
    Purchase a product directly. 
    
    Args:
        asin: The product asin.
        quantity: The quantity to purchase.
        shipping_address: The shipping address.
        user: The user information.
        product_url: The URL of the product in amazon.

    Example:
        shipping_address = {
            "full_name": "John Doe",
            "address": "123 Main St",
            "city": "New York",
            "state": "NY",
            "country": "US",
            "postalCode": "10001"
        }
        
        user = {
            "full_name": "John Doe",
            "email": "john@example.com",
        }

        
        a.buy_now(product_id, variant_id, shipping_address, user, product_url)
        
    Returns:
        The payment information.
    """
    # Amazon has 2 types of URLS:
    # the Detail page (/dp/) = "https://www.amazon.com/dp/B0C9S88QV6"
    # the one returned by search api https://www.amazon.com/FLEXISPOT-Upgraded-Bre....
        
    request_data = {
        "external_id": asin,
        "quantity": quantity,
        "shipping_address": shipping_address,
        "user": user,
        "product_url": product_url
    }
    
    return self._make_request("POST", "buy-now", json=request_data)

