@patch
def buy_now_with_x402(self: Amazon, asin: str, shipping_address: Dict,
            user: Dict, quantity: int = 1, x_payment: str | None = None, product_url: str = '') -> Dict:
    """
    Purchase a product directly. 
    
    Args:
        asin: The product asin.
        quantity: The quantity to purchase.
        shipping_address: The shipping address.
        user: The user information.
        product_url: The URL of the product in amazon.
        
    Example:
        shipping_address = {
            "full_name": "John Doe",
            "address": "123 Main St",
            "city": "New York",
            "state": "NY",
            "country": "US",
            "postalCode": "10001"
        }
        
        user = {
            "full_name": "John Doe",
            "email": "john@example.com",
        }
        
        a.buy_now(product_id, variant_id, shipping_address, user)
        
    Returns:
        The payment information.
    """

        
    request_data = {
        "external_id": asin,
        "quantity": quantity,
        "shipping_address": shipping_address,
        "user": user,
        "product_url": product_url
    }
    headers = {
        "Payment-Protocol": "x402",
    }
    if x_payment:
        headers["X-PAYMENT"] = x_payment
    
    return self._make_request(
        "POST",
        "buy-now",
        json=request_data,
        headers=headers
    )

