    isin: str = Field(default=None, description='Filter by international securities identification number (ISIN)', examples=['US0378331005'])
    cusip: str = Field(default=None, description='The CUSIP of an instrument for which data is requested', examples=['594918104'])
    exchange: str = Field(default=None, description='Exchange where instrument is traded')
