from collections.abc import Mapping
from typing import TYPE_CHECKING, Any, TypeVar, Union

from attrs import define as _attrs_define

from ..types import UNSET, Unset

if TYPE_CHECKING:
    from ..models.doc_alt_text import DocAltText
    from ..models.doc_sentence import DocSentence
    from ..models.imported_doc_annotation import ImportedDocAnnotation
    from ..models.imported_doc_category import ImportedDocCategory
    from ..models.imported_document_metadata import ImportedDocumentMetadata


T = TypeVar("T", bound="ImportedDocument")


@_attrs_define
class ImportedDocument:
    """
    Attributes:
        identifier (str): document identifier (externally provided or generated by sherpa)
        text (str): text of the document
        title (str): title of the document
        alt_texts (Union[Unset, list['DocAltText']]):
        annotations (Union[Unset, list['ImportedDocAnnotation']]):
        categories (Union[Unset, list['ImportedDocCategory']]):
        metadata (Union[Unset, ImportedDocumentMetadata]): document metadata
        sentences (Union[Unset, list['DocSentence']]):
    """

    identifier: str
    text: str
    title: str
    alt_texts: Union[Unset, list["DocAltText"]] = UNSET
    annotations: Union[Unset, list["ImportedDocAnnotation"]] = UNSET
    categories: Union[Unset, list["ImportedDocCategory"]] = UNSET
    metadata: Union[Unset, "ImportedDocumentMetadata"] = UNSET
    sentences: Union[Unset, list["DocSentence"]] = UNSET

    def to_dict(self) -> dict[str, Any]:
        identifier = self.identifier

        text = self.text

        title = self.title

        alt_texts: Union[Unset, list[dict[str, Any]]] = UNSET
        if not isinstance(self.alt_texts, Unset):
            alt_texts = []
            for alt_texts_item_data in self.alt_texts:
                alt_texts_item = alt_texts_item_data.to_dict()
                alt_texts.append(alt_texts_item)

        annotations: Union[Unset, list[dict[str, Any]]] = UNSET
        if not isinstance(self.annotations, Unset):
            annotations = []
            for annotations_item_data in self.annotations:
                annotations_item = annotations_item_data.to_dict()
                annotations.append(annotations_item)

        categories: Union[Unset, list[dict[str, Any]]] = UNSET
        if not isinstance(self.categories, Unset):
            categories = []
            for categories_item_data in self.categories:
                categories_item = categories_item_data.to_dict()
                categories.append(categories_item)

        metadata: Union[Unset, dict[str, Any]] = UNSET
        if not isinstance(self.metadata, Unset):
            metadata = self.metadata.to_dict()

        sentences: Union[Unset, list[dict[str, Any]]] = UNSET
        if not isinstance(self.sentences, Unset):
            sentences = []
            for sentences_item_data in self.sentences:
                sentences_item = sentences_item_data.to_dict()
                sentences.append(sentences_item)

        field_dict: dict[str, Any] = {}
        field_dict.update(
            {
                "identifier": identifier,
                "text": text,
                "title": title,
            }
        )
        if alt_texts is not UNSET:
            field_dict["altTexts"] = alt_texts
        if annotations is not UNSET:
            field_dict["annotations"] = annotations
        if categories is not UNSET:
            field_dict["categories"] = categories
        if metadata is not UNSET:
            field_dict["metadata"] = metadata
        if sentences is not UNSET:
            field_dict["sentences"] = sentences

        return field_dict

    @classmethod
    def from_dict(cls: type[T], src_dict: Mapping[str, Any]) -> T:
        from ..models.doc_alt_text import DocAltText
        from ..models.doc_sentence import DocSentence
        from ..models.imported_doc_annotation import ImportedDocAnnotation
        from ..models.imported_doc_category import ImportedDocCategory
        from ..models.imported_document_metadata import ImportedDocumentMetadata

        d = dict(src_dict)
        identifier = d.pop("identifier")

        text = d.pop("text")

        title = d.pop("title")

        alt_texts = []
        _alt_texts = d.pop("altTexts", UNSET)
        for alt_texts_item_data in _alt_texts or []:
            alt_texts_item = DocAltText.from_dict(alt_texts_item_data)

            alt_texts.append(alt_texts_item)

        annotations = []
        _annotations = d.pop("annotations", UNSET)
        for annotations_item_data in _annotations or []:
            annotations_item = ImportedDocAnnotation.from_dict(annotations_item_data)

            annotations.append(annotations_item)

        categories = []
        _categories = d.pop("categories", UNSET)
        for categories_item_data in _categories or []:
            categories_item = ImportedDocCategory.from_dict(categories_item_data)

            categories.append(categories_item)

        _metadata = d.pop("metadata", UNSET)
        metadata: Union[Unset, ImportedDocumentMetadata]
        if isinstance(_metadata, Unset):
            metadata = UNSET
        else:
            metadata = ImportedDocumentMetadata.from_dict(_metadata)

        sentences = []
        _sentences = d.pop("sentences", UNSET)
        for sentences_item_data in _sentences or []:
            sentences_item = DocSentence.from_dict(sentences_item_data)

            sentences.append(sentences_item)

        imported_document = cls(
            identifier=identifier,
            text=text,
            title=title,
            alt_texts=alt_texts,
            annotations=annotations,
            categories=categories,
            metadata=metadata,
            sentences=sentences,
        )

        return imported_document
